import { renderChart } from "@trebor/zoomable-voronoi-treemap"







md`### Overview`







data = {
  const flare = await d3.json(
    "https://raw.githubusercontent.com/vanHeemstraSystems/learning-d3-voronoi-treemap/main/data/topics.json"
  );
  return d3.hierarchy(flare).sum((d) =&gt; (d.children ? 0 : Math.random()));
}







__topicsChart = {
  return renderChart({
    data,
    size: { width, height: 400 }
  });
}
